import { styled, keyframes, css } from "styled-components";
import Header from "../Checklist/Header";
import Calendar from "react-calendar";
import { useEffect, useState } from "react";
import { Icon } from "react-icons-kit";
import { trashO } from "react-icons-kit/fa/trashO";
import { plus } from "react-icons-kit/fa/plus";
import { edit } from "react-icons-kit/fa/edit";
import { check } from "react-icons-kit/fa/check";

import { UserContext } from "../../UserContext";
import { useContext } from "react";

const CheckList = () => {
  const { currentUser, setCurrentUser } = useContext(UserContext);
  const [value, onChange] = useState(new window.Date());
  const [task, setTask] = useState({});
  const [newTask, setNewTask] = useState("");
  const [editingTask, setEditingTask] = useState({});

  const Refresh = () => {
    fetch(`/tasklist/${value.toString().slice(0, 15)}/${currentUser.email}`)
      .then((response) => response.json())
      .then((parsed) => {
        if (parsed.status === 404) {
          throw new Error(parsed.message);
        }
        setTask(parsed.data.task);
      })
      .catch((error) => {
        setTask([]);
        console.log("Error fetching data:", error);
      });
  };

  useEffect(() => {
    if (currentUser) {
      Refresh();
    }
  }, [value, currentUser]);

  const handleDelete = (index) => {
    fetch(
      `/deletetask/${value.toString().slice(0, 15)}/${
        currentUser.email
      }/${index}`,
      {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((parsed) => {
        console.log(parsed);
        Refresh();
      })

      .catch((error) => console.log(error));
  };

  // const handleUpdate = (input, index) => {
  //   setTask({});
  //   fetch("/updatetask", {
  //     method: "PATCH",
  //     headers: {
  //       Accept: "application/json",
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify({
  //       date: value.toString().slice(0, 15),
  //       user: currentUser.email,
  //       input: input,
  //       index: index,
  //     }),
  //   })
  //     .then((response) => response.json())
  //     .then((parsed) => {
  //       console.log(parsed);
  //       Refresh();
  //     })

  //     .catch((error) => console.log(error));
  // };
  const handleUpdate = (input, index, checked) => {
    const updatedTask = {
      ...task,
      [index]: {
        input: input.input,
        checked,
      },
    };
    setTask(updatedTask);

    fetch("/updatetask", {
      method: "PATCH",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        date: value.toString().slice(0, 15),
        user: currentUser.email,
        input: input.input,
        index: index,
        checked: checked,
      }),
    })
      .then((response) => response.json())
      .then((parsed) => {
        console.log(parsed);
        Refresh();
      })
      .catch((error) => console.log(error));
  };

  const handleEdit = (key) => {
    const taskItem = task[key];
    if (taskItem) {
      setEditingTask(taskItem);
      handleUpdate(taskItem, key, false);
    }
  };

  const handleCheckmark = (key) => {
    handleUpdate(task[key], key, true);
  };

  const handleSubmit = (input, index) => {
    if (input.length > 0) {
      fetch("/addtask", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          date: value.toString().slice(0, 15),
          user: currentUser.email,
          input: input,
          index: index,
        }),
      })
        .then((response) => response.json())
        .then((parsed) => {
          console.log(parsed);
          setNewTask("");
          Refresh();
        })

        .catch((error) => console.log(error));
    }
  };

  return (
    <Main>
      <Header />
      <Body>
        <CalendarContainer>
          <Calendar onChange={onChange} value={value} />
        </CalendarContainer>

        <ListContainer>
          <ListHeader>
            <Date> {value.toString().slice(0, 15)} </Date>
            <Share>Share</Share>
          </ListHeader>
          <List>
            <AddTask>
              <Input
                onChange={(event) => {
                  setNewTask(event.target.value);
                }}
                value={newTask}
              />
              <AddButton
                onClick={() =>
                  handleSubmit(newTask, Object.values(task).length)
                }
              >
                <Icon icon={plus} size={50} />
              </AddButton>
            </AddTask>
            {Object.keys(task).map((item, index) => {
              const { input, checked } = item;

              return (
                <Tasklines key={index}>
                  <Edit onClick={() => handleEdit(index)}>
                    {" "}
                    <Icon icon={edit} size={50} />
                  </Edit>
                  {index !== editingTask ? (
                    <TaskText>{input}</TaskText>
                  ) : (
                    <TaskInput
                      onChange={(event) => {
                        handleUpdate(
                          { input: event.target.value },
                          index,
                          item.checked
                        );
                        setEditingTask(event.target.value);
                        // setTask({ ...task, [key]: event.target.value });
                      }}
                      // checked={checkedTasks[index]}
                      checked={checked}
                      value={input}
                      placeholder={input}
                    />
                  )}
                  <Delete
                    onClick={() => {
                      handleDelete(index);
                    }}
                  >
                    <Icon icon={trashO} size={50} />
                  </Delete>
                  <Checkmark onClick={() => handleCheckmark(index)}>
                    <Icon icon={check} size={50} />
                  </Checkmark>
                </Tasklines>
              );
            })}
          </List>
        </ListContainer>

        <GradientBox></GradientBox>
      </Body>
    </Main>
  );
};

export default CheckList;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  position: relative;
  // align-items: center;
`;

const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 85%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #00c4cc, #ff66c4, pink);
  background-size: 200% 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;

const CalendarContainer = styled.div`
  max-width: 25%;
  height: fit-content;
  margin: 5%;
  background-color: #00c4cc;
  color: white;
  border-radius: 0.5rem;
  font-size: 1.5rem;

  .react-calendar__navigation {
    display: flex;
  }
  .react-calendar__navigation__label {
    font-weight: bold;
    font-size: 1.5rem;
  }
  .react-calendar__navigation__arrow {
