import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";

const Projects = () => {
  const { user } = useAuth0();
  return (
    <Main>
      <Header />
      <Body>
        <SideBar>
          <NewProject>Add Project</NewProject>
        </SideBar>

        <ProjectsOverview>
          <Text>Project Overview</Text>
          <ProjectsTable></ProjectsTable>
        </ProjectsOverview>

        <GradientBox></GradientBox>
      </Body>
    </Main>
  );
};

export default Projects;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  position: relative;
  align-items: center;
`;
const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 70%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #c1ff72, #ffde59);
  background-size: 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;
const SideBar = styled.div`
  max-width: 25%;
  height: fit-content;
  margin: 5%;
  color: white;
  border-radius: 0.5rem;
  font-size: 1.5rem;
  height: 100%;
  width: 20%;
  border: 2rem dashed #00c4cc;
`;

const NewProject = styled.button`
  font-size: 2rem;
  color: white;
  border-radius: 0.5rem;
  background-color: orange;
  border: none;
  padding: 1rem;
  margin: 50%;
  width: 70%;

  &:hover {
    background-color: #cb6ce6;
  }

  &:active {
    background-color: pink;
  }
`;
const ProjectsOverview = styled.div`
  z-inex: 2;
  width: 50%;
  height: 100%;
  margin: 5%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 0.5rem;
`;

const Text = styled.div``;
const ProjectsTable = styled.div``;
