import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useParams } from "react-router-dom";

import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";

const CreateBoard = () => {
  const [columns, setColumns] = useState({
    requested: {
      name: "Requested",
      items: [
        { id: "1", content: "First Task" },
        { id: "2", content: "Second Task" },
        { id: "3", content: "Third Task" },
        { id: "4", content: "Fourth Task" },
      ],
    },
    toDo: {
      name: "To do",
      items: [],
    },
    inProgress: {
      name: "In Progress",
      items: [],
    },
    done: {
      name: "Done",
      items: [],
    },
  });

  const { user } = useAuth0();
  useEffect(() => {
    fetch("/")
      .then((response) => {
        const { tasks, taskStatus } = response.data;
        setColumns(taskStatus);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const onDragEnd = (result, columns, setColumns) => {
    if (!result.destination) return;
    const { source, destination } = result;

    if (source.droppableId !== destination.droppableId) {
      const sourceColumn = columns[source.droppableId];
      const destColumn = columns[destination.droppableId];
      const sourceItems = [...sourceColumn.items];
      const destItems = [...destColumn.items];
      const [removed] = sourceItems.splice(source.index, 1);
      destItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...sourceColumn,
          items: sourceItems,
        },
        [destination.droppableId]: {
          ...destColumn,
          items: destItems,
        },
      });
    } else {
      const column = columns[source.droppableId];
      const copiedItems = [...column.items];
      const [removed] = copiedItems.splice(source.index, 1);
      copiedItems.splice(destination.index, 0, removed);
      setColumns({
        ...columns,
        [source.droppableId]: {
          ...column,
          items: copiedItems,
        },
      });
    }
  };

  return (
    <Main>
      <div>
        <Header />
        <Body>
          <SideBar>
            <SideBarContent>Project Title</SideBarContent>
            <SideBarContent>Members Names</SideBarContent>
            <SideBarContent>Project Due Date </SideBarContent>
          </SideBar>
          <>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                height: "200px",
                backgroundColor: "red",
              }}
            >
              <DragDropContext onDragEnd={onDragEnd}>
                {Object.entries(columns).map(([columnId, column]) => (
                  <div key={columnId}>
                    <h2>{column.name}</h2>
                    <Droppable droppableId={columnId}>
                      {(provided) => (
                        <ItemList
                          ref={provided.innerRef}
                          {...provided.droppableProps}
                        >
                          {column.items.map((item, index) => (
                            <Draggable
                              key={item.id}
                              draggableId={item.id}
                              index={index}
                            >
                              {(provided) => (
                                <Item
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                >
                                  {item.content}
                                </Item>
                              )}
                            </Draggable>
                          ))}
                          {provided.placeholder}
                        </ItemList>
                      )}
                    </Droppable>
                  </div>
                ))}
              </DragDropContext>
            </div>
          </>
        </Body>
      </div>
    </Main>
  );
};
export default CreateBoard;

const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  width: 80%;
  height: 100%;
  display: flex;
  border: 1rem solid #00c4cc;
  justify-content: center;
  margin: 10%;
`;
const SideBar = styled.div`
  max-width: 25%;
  border-radius: 0.5rem;
  width: 20%;
  border-right: 1rem solid #00c4cc;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
`;

const SideBarContent = styled.div`
  border: 0.5rem solid orange;
  padding: 1rem;
  color: black;
  width: 45%;
  margin: 10%;
`;
// const Container = styled.div`
// display: flex,
// justify-content: center,
// height: 500px,
// align-items:center;
// background-color:red;

// `;

const ItemList = styled.div`
  background-color: white;
  padding: 8px;
  width: 250px;
  min-height: 500px;
  background-color: pink;
`;

const Item = styled.div`
  user-select: none;
  padding: 16px;
  margin-bottom: 8px;
  min-height: 50px;
  background-color: #456c86;
  color: purple;
`;
