import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import { useState, useEffect } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { plus } from "react-icons-kit/fa/plus";
import { Icon } from "react-icons-kit";

const taskStatus = {
  requested: {
    name: "Requested",
    items: [],
  },
  toDo: {
    name: "To do",
    items: [],
  },
  inProgress: {
    name: "In Progress",
    items: [],
  },
  done: {
    name: "Done",
    items: [],
  },
};

const onDragEnd = (result, columns, setColumns) => {
  if (!result.destination) return;
  const { source, destination } = result;

  if (source.droppableId !== destination.droppableId) {
    const sourceColumn = columns[source.droppableId];
    const destColumn = columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        items: sourceItems,
      },
      [destination.droppableId]: {
        ...destColumn,
        items: destItems,
      },
    });
  } else {
    const column = columns[source.droppableId];
    const copiedItems = [...column.items];
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...column,
        items: copiedItems,
      },
    });
  }
};

const CreateBoard = () => {
  const { user } = useAuth0();
  const [columns, setColumns] = useState(taskStatus);
  const [startDate, setStartDate] = useState(new Date());
  const [formData, setFormData] = useState({});
  const [addTask, setAddTask] = useState({});
  const navigate = useNavigate();
  const [userList, setUserList] = useState([]);

  useEffect(() => {
    fetch("/getUser")
      .then((response) => response.json())
      .then((parsed) => {
        console.log(parsed.data);
        setUserList(parsed.data);
      });
  }, []);

  const addTaskhandleChange = (event) => {
    setAddTask({
      ...addTask,
      [event.target.id]: event.target.value,
    });
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.id]: e.target.value,
    });
  };

  const handleAddTask = (id, start) => {
    const name = columns[id].name;
    const newArray = [...columns[id].items];
    newArray.push({ id: newArray.length + 1, content: addTask[id] });
    setColumns({ ...columns, [id]: { name: name, items: newArray } });
    setAddTask({});
  };

  const handleDelete = (columnId, index) => {
    const name = columns[columnId].name;
    const newArray = [...columns[columnId].items];

    newArray.splice(index, 1);
    setColumns({ ...columns, [columnId]: { name: name, items: newArray } });
    setAddTask({});
  };
  const handleSubmit = (e) => {
    fetch("/addProject", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        title: formData.projectTitle,
        description: formData.projectDescription,
        dueDate: formData.start,
        taskData: columns,
        user: user.email,
      }),
    })
      .then((res) => res.json())
      .then((parsed) => {
        if (parsed.status === 200) {
          console.log(parsed.data);
          navigate("/projects");
        } else {
          alert(parsed.error);
        }
      });
  };
  return (
    <Main>
      <div>
        <Header />
        <Body>
          {formData && (
            <SideBar>
              <FormContainer>
                <Form onSubmit={handleSubmit}>
                  <label>Project Title</label>
                  <FormInput
                    id="projectTitle"
                    name="projectTitle"
                    type="text"
                    onChange={handleChange}
                  />

                  <label>Members Names</label>
                  <select
                    id="membersName"
                    name="membersName"
                    type="text"
                    onChange={handleChange}
                    style={{
                      padding: "2%",
                      fontSize: "1.5rem",
                      borderRadius: "0.5rem",
                      border: "none",
                      boxShadow:
                        "0 0.1rem 0.2rem 0 #808080, 0 0.1rem 0.2rem #808080",
                    }}
                  >
                    {userList?.map((member) => {
                      return <option value="">{member}</option>;
                    })}
                  </select>

                  <label>Project Description</label>
                  <FormInput
                    id="projectDescription"
                    name="projectDescription"
                    type="text"
                    onChange={handleChange}
                  />

                  <label for="start">Project Due Date</label>
                  <FormInput
                    type="date"
                    id="start"
                    name="start"
                    value={formData.start}
                    onChange={handleChange}
                  />
                </Form>
              </FormContainer>
              <SaveLink type="submit" onClick={handleSubmit}>
                Save
              </SaveLink>
            </SideBar>
          )}
          <div>
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                height: "100%",
              }}
            >
              <DragDropContext
                onDragEnd={(result) => onDragEnd(result, columns, setColumns)}
              >
                {Object.entries(columns).map(([columnId, column], index) => (
                  <div
                    key={columnId}
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      alignItems: "center",
                    }}
                  >
                    <BoardHeader>{column.name}</BoardHeader>

                    <Droppable droppableId={columnId}>
                      {(provided) => (
                        <ItemList
                          ref={provided.innerRef}
                          {...provided.droppableProps}
                        >
                          {column.items.map((item, index) => (
                            <Draggable
                              key={item.id}
                              draggableId={item.id.toString()}
                              index={index}
                            >
                              {(provided) => (
                                <Item
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                >
                                  {item.content}
                                  <Delete
                                    onClick={() =>
                                      handleDelete(columnId, index)
                                    }
                                  >
                                    delete
                                  </Delete>
                                </Item>
                              )}
                            </Draggable>
                          ))}
                          <NewTaskContainer>
                            <NewTaskInput
                              type="text"
                              id={columnId}
                              value={addTask[columnId]}
                              onChange={addTaskhandleChange}
                            />
                            <NewTaskInput
                              type="date"
                              name="start"
                              id="start"
                              value={formData.start}
                              onChange={handleChange}
                            />

                            <NewTask
                              onClick={() =>
                                handleAddTask(columnId, formData.start)
                              }
                            >
                              {" "}
                              New Task{" "}
                            </NewTask>
                          </NewTaskContainer>

                          {provided.placeholder}
                        </ItemList>
                      )}
                    </Droppable>
                  </div>
                ))}
              </DragDropContext>
            </div>
          </div>
        </Body>
      </div>
      <GradientBox></GradientBox>
    </Main>
  );
};
