import React, { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";

const Projects = ({ boardId, projectId }) => {
  const { user } = useAuth0();
  const navigate = useNavigate();

  const [projects, setProjects] = useState([]);

  useEffect(() => {
    if (user) {
      fetch(`/getProjects/${user.email}`)
        .then((response) => response.json())
        .then((parsed) => {
          setProjects(parsed.data);
        });
    }
  }, [user]);

  return (
    <div className="main">
      <Header />
      <div className="body">
        <div className="projects-overview">
          <div className="text">Projects Overview</div>
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th align="center">Project Title</th>
                  <th align="center">Due Date</th>
                  <th align="center">Project Description</th>
                  <th align="center">Day's Left to Complete</th>
                  <th align="center">Status</th>
                  <th align="center"></th>
                </tr>
              </thead>
              <tbody>
                {projects.map((project, index) => {
                  return (
                    <tr
                      key={index}
                      onClick={() => {
                        navigate(`/projectdetails/${projectId}`);
                      }}
                    >
                      <td align="center">{project.title}</td>
                      <td align="center">{project.dueDate}</td>
                      <td align="center">{project.description}</td>
                      <td align="center">24days</td>
                      <td align="center">{project.status}</td>
                      <td align="center">
                        <button
                          className="delete"
                          onClick={() => {
                            projects.splice(index, 1);
                            setProjects([...projects]);
                          }}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
        </div>
        <div className="add-project">
          <button
            className="create-project"
            onClick={() => {
              navigate(`/createboard/${boardId}`);
            }}
          >
            Add New Project
          </button>
        </div>
        <div className="gradient-box"></div>
      </div>
    </div>
  );
};

export default Projects;
