import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import Calendar from "react-calendar";
import { useEffect, useState } from "react";
import { Icon } from "react-icons-kit";
import { trashO } from "react-icons-kit/fa/trashO";

const CheckList = () => {
  const [value, onChange] = useState("2017-01-01");
  const [task, setTask] = useState();
  useEffect(() => {
    fetch(`/tasklist/${value.toString().slice(0, 15)}/user_id`)
      .then((response) => response.json())
      .then((parsed) => {
        if (parsed.status === 404) {
          throw new Error(parsed.message);
        }

        setTask(parsed.data.task);
      })
      .catch((error) => {
        setTask([]);
        console.log("Error fetching data:", error);
      });
  }, [value]);

  return (
    <Main>
      <Header />
      <Body>
        <CalendarContainer>
          <Calendar onChange={onChange} value={value} />
        </CalendarContainer>

        <List>
          <ListHeader>
            <Date> {value.toString().slice(0, 15)} </Date>
            <Share>Share</Share>
          </ListHeader>
          <ListTasks>
            <Taskline>
              <Delete>
                <Icon icon={trashO} size={50} />
              </Delete>
              <CheckBox></CheckBox>
              HELLLO
            </Taskline>
            <AddTask>Add Task</AddTask>
          </ListTasks>
        </List>

        <GradientBox></GradientBox>
      </Body>
    </Main>
  );
};

export default CheckList;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  position: relative;
  align-items: center;
`;

const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 45%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #00c4cc, #ff66c4);
  background-size: 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;

const CalendarContainer = styled.div`
  max-width: 30%;
  height: fit-content;
  margin: 5%;
  background-color: #00c4cc;
  color: white;
  border-radius: 0.5rem;
  font-size: 1.5rem;

  .react-calendar__navigation {
    display: flex;
  }
  .react-calendar__navigation__label {
    font-weight: bold;
    font-size: 1.5rem;
  }
  .react-calendar__navigation__arrow {
    flex-grow: 0.333;
  }
  .react-calendar__month-view__weekdays {
    text-align: center;
    text-decoration: none;
  }

  button {
    font-size: 1.5rem;
    background-color: transparent;
    border: 0;
    border-radius: 3px;
    color: white;
    padding: 0.5rem 0.05rem;

    &:hover {
      background-color: #cb6ce6;
    }

    &:active {
      background-color: pink;
    }
  }
  .react-calendar__month-view__days__day--neighboringMonth {
    opacity: 0.5;
  }
  .react-calendar__month-view__days__day--weekend {
    color: #dfdfdf;
  }
`;

const List = styled.div`
  z-inex: 2;
  width: 70%;
  height: 100%;
  margin: 2rem;
  display: flex;
  flex-direction: column;
`;
const ListHeader = styled.div`
  color: white;
  border-radius: 0.5rem;
  display: flex;
  font-size: 2rem;
  height: 10%;
  align-items: center;
  justify-content: space-between;
  padding: 1rem;
  margin: 5%;
  background-color: #00c4cc;
`;

const Date = styled.div`
  padding: 1rem;
`;
const Share = styled.button`
  font-size: 2rem;
  color: white;
  border-radius: 0.5rem;
  background-color: transparent;
  border: none;
  padding: 1rem;

  &:hover {
    background-color: #cb6ce6;
  }

  &:active {
    background-color: pink;
  }
`;
const ListTasks = styled.div`
  background-image: url("./images/stickynote.png");
  display: flex;
  height: 100%;
  margin: 2rem;
  position: relative;
`;

const Taskline = styled.div``;
const Delete = styled.button`
  background-color: transparent;
  border: none;
  &:hover {
    color: green;
  }
`;
const CheckBox = styled.div`
  position: absolute;
  margin: 2rem;
  height: 2rem;
  width: 2rem;
  background-color: #eee;
  &:hover {
    background-color: green;
  }
`;

const AddTask = styled.div``;
