import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import { useEffect, useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import {
  TableContainer,
  Table,
  TableHead,
  TableBody,
  TableRow,
  TableCell,
  Paper,
} from "@material-ui/core";

const Projects = () => {
  const { user } = useAuth0();
  return (
    <Main>
      <div>
        <Header />
        <Body>
          <SideBar>
            <NewProject>Create Project</NewProject>
          </SideBar>

          <ProjectsOverview>
            <Text>Projects Overview</Text>
            <TableContainer component={Paper}>
              <ProjectsTable>
                <TableHead>
                  <TableRow>
                    <HeadCells>Project Title</HeadCells>
                    {/* <Header>Due Date</Header>
                    <Header>Project Description</Header>
                    <Header>Members</Header>
                    <Header>Day's Left to Complete</Header>
                    <Header>Status</Header> */}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {/* {tableData.map((row) => ( */}
                  <TableRow
                    // key={row.id}
                    sx={{ "&:last-child td, &last-child th": { border: 0 } }}
                  >
                    {/* <TableHeader>{row.id}</TableHeader>
                    <TableHeader>{row.first_name}</TableHeader>
                    <TableHeader>{row.last_name}</TableHeader>
                    <TableHeader>{row.email}</TableHeader> */}
                  </TableRow>
                  {/* ))} */}
                </TableBody>
              </ProjectsTable>
            </TableContainer>
          </ProjectsOverview>

          <GradientBox></GradientBox>
        </Body>
      </div>
    </Main>
  );
};

export default Projects;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
  background-color: red;
`;

const Body = styled.div`
  width: 80%;
  height: 100%;
  display: flex;
  position: relative;
  border: 1rem solid #00c4cc;
  margin: 5%;
`;
const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 70%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #c1ff72, #ffde59);
  background-size: 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;
const SideBar = styled.div`
  max-width: 25%;
  color: white;
  border-radius: 0.5rem;
  width: 20%;
  border-right: 1rem solid #00c4cc;
`;

const NewProject = styled.button`
  font-size: 2rem;
  color: white;
  border-radius: 0.5rem;
  background-color: orange;
  border: none;
  padding: 1rem;
  margin: 20%;
  padding: 2%;

  &:hover {
    background-color: yellow;
    color: black;
  }

  &:active {
    background-color: yellow;
  }
`;
const ProjectsOverview = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  margin: 5%;
  gap: 2rem;
  border-radius: 0.5rem;
  color: orange;
  font-size: 2rem;
  height: 100%;
  width: 100%;
`;

const Text = styled.div``;
const ProjectsTable = styled(Table)``;
const HeadCells = styled(TableCell)`
  color: purple;
  font-size: 5rem;
`;
