import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import Calendar from "react-calendar";
import { useEffect, useState } from "react";
import { Icon } from "react-icons-kit";
import { trashO } from "react-icons-kit/fa/trashO";
import { plus } from "react-icons-kit/fa/plus";
import { edit } from "react-icons-kit/fa/edit";

import { UserContext } from "../../UserContext";
import { useContext } from "react";

const CheckList = () => {
  const { currentUser, setCurrentUser } = useContext(UserContext);
  const [value, onChange] = useState(new window.Date());
  const [task, setTask] = useState({});
  const [newTask, setNewTask] = useState("");

  const Refresh = () => {
    fetch(`/tasklist/${value.toString().slice(0, 15)}/${currentUser.email}`)
      .then((response) => response.json())
      .then((parsed) => {
        if (parsed.status === 404) {
          throw new Error(parsed.message);
        }
        setTask(parsed.data.task);
      })
      .catch((error) => {
        setTask([]);
        console.log("Error fetching data:", error);
      });
  };

  useEffect(() => {
    if (currentUser) {
      Refresh();
    }
  }, [value, currentUser]);

  const handleDelete = (index) => {
    fetch(
      `/deletetask/${value.toString().slice(0, 15)}/${
        currentUser.email
      }/${index}`,
      {
        method: "DELETE",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((parsed) => {
        console.log(parsed);
        Refresh();
      })

      .catch((error) => console.log(error));
  };

  const handleUpdate = (input, index) => {
    setTask({});
    fetch("/updatetask", {
      method: "PATCH",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        date: value.toString().slice(0, 15),
        user: currentUser.email,
        input: input,
        index: index,
      }),
    })
      .then((response) => response.json())
      .then((parsed) => {
        console.log(parsed);
        Refresh();
      })

      .catch((error) => console.log(error));
  };
  const handleSubmit = (input, index) => {
    if (input.length > 0) {
      fetch("/addtask", {
        method: "POST",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          date: value.toString().slice(0, 15),
          user: currentUser.email,
          input: input,
          index: index,
        }),
      })
        .then((response) => response.json())
        .then((parsed) => {
          console.log(parsed);
          setNewTask("");
          Refresh();
        })

        .catch((error) => console.log(error));
    }
  };
  const handleCheckboxChange = (index) => {
    const updatedTask = { ...task };
    updatedTask[index].completed = !updatedTask[index].completed;
    setTask(updatedTask);
  };

  return (
    <Main>
      <Header />
      <Body>
        <CalendarContainer>
          <Calendar onChange={onChange} value={value} />
        </CalendarContainer>

        <ListContainer>
          <ListHeader>
            <Date> {value.toString().slice(0, 15)} </Date>
            <Share>Share</Share>
          </ListHeader>
          <List>
            <AddTask>
              <Input
                onChange={(event) => {
                  setNewTask(event.target.value);
                }}
                value={newTask}
              />
              <AddButton
                onClick={() =>
                  handleSubmit(newTask, Object.values(task).length)
                }
              >
                <Icon icon={plus} size={50} />
              </AddButton>
            </AddTask>
            {Object.keys(task).map((key, index) => {
              return (
                <Tasklines>
                  <Edit onClick={() => handleUpdate(task[key], key)}>
                    {" "}
                    <Icon icon={edit} size={50} />
                  </Edit>
                  <Checkbox
                    type="checkbox"
                    checked={task[key].completed}
                    onChange={() => handleCheckboxChange(key)}
                  />

                  <TaskInput
                    onChange={(event) => {
                      setTask({
                        ...task,
                        [key]: { ...task[key], name: event.target.value },
                      });
                    }}
                    value={task[key].name}
                    placeholder={task[key].name}
                    completed={task[key].completed}
                  />

                  <Delete
                    onClick={() => {
                      handleDelete(key);
                    }}
                  >
                    <Icon icon={trashO} size={50} />
                  </Delete>
                </Tasklines>
              );
            })}
          </List>
        </ListContainer>

        <GradientBox></GradientBox>
      </Body>
    </Main>
  );
};

export default CheckList;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  height: 100%;
  width: 100%;
  display: flex;
  position: relative;
  // align-items: center;
`;

const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 70%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #00c4cc, #ff66c4, pink);
  background-size: 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;

const CalendarContainer = styled.div`
  max-width: 25%;
  height: fit-content;
  margin: 5%;
  background-color: #00c4cc;
  color: white;
  border-radius: 0.5rem;
  font-size: 1.5rem;

  .react-calendar__navigation {
    display: flex;
  }
  .react-calendar__navigation__label {
    font-weight: bold;
    font-size: 1.5rem;
  }
  .react-calendar__navigation__arrow {
    flex-grow: 0.333;
  }
  .react-calendar__month-view__weekdays {
    text-align: center;
    text-decoration: none;
  }

  button {
    font-size: 1.5rem;
    background-color: transparent;
    border: 0;
    border-radius: 3px;
    color: white;
    padding: 0.5rem 0.05rem;

    &:hover {
      background-color: #cb6ce6;
    }

    &:active {
      background-color: pink;
    }
  }
  .react-calendar__month-view__days__day--neighboringMonth {
    opacity: 0.5;
  }
  .react-calendar__month-view__days__day--weekend {
    color: #dfdfdf;
  }
`;

const ListContainer = styled.div`
  background-image: url("./images/stickyn.png");
  background-size: contain;
  background-repeat: no-repeat;
  z-inex: 2;
  width: 70%;
  height: 100%;
  margin: 5%;
  display: flex;
  flex-direction: column;
  gap: 5%;
  align-items: flex-start;
`;
const ListHeader = styled.div`
  color: white;
  border-radius: 0.5rem;
  display: flex;
  font-size: 2rem;
  height: 5%;
  width: 50%;
  align-items: center;
  justify-content: space-around;
  padding: 1rem;
  gap: 5%;
  margin-top: 5%;
  margin-left: 5%;
  background-color: #cb6ce6;
`;

const Date = styled.div``;
const Share = styled.button`
  font-size: 2rem;
  color: white;
  border-radius: 0.5rem;
  background-color: transparent;
  border: none;
  padding: 1rem;

  &:hover {
    background-color: #cb6ce6;
  }

  &:active {
    background-color: pink;
  }
`;
const List = styled.div`
  display: flex;
  flex-direction: column;
  gap: 5%;
  height: 100%;
  width: 60%;
`;
const AddTask = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;
const Input = styled.input`
  width: 80%;
  padding: 1%;
  font-size: 2rem;
  border-radius: 0.5rem;
  border: none;
  // box-shadow: 0 0.1rem 0.2rem 0 #808080, 0 0.1rem 0.2rem #808080;
  background-color: #00c4cc;
`;
const AddButton = styled.button`
  background-color: transparent;
  border: none;
  width: 5%;
  &:hover {
    color: #cb6ce6;
  }
  &:active {
    scale: 0.9;
  }
`;

const Tasklines = styled.ul`
  width: 100%;
  height: 5%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  list-style: none;
`;
const Checkbox = styled.edit`
  margin-right: 0.5rem;
`;

const TaskInput = styled.input`
  width: 70%;
  padding: 1%;
  font-weight: bold;
  font-size: 2rem;
  border-radius: 0.5rem;
  border: none;
  background-color: ${(props) => (props.completed ? "#d3d3d3" : "#00c4cc")};
  margin: 1.5%;
`;

const Delete = styled.button`
  background-color: transparent;
  border: none;
  width: 5%;
  &:hover {
    color: red;
  }
`;

const Edit = styled.div`
  margin: 0.1rem;
  width: 5%;
  &:hover {
    color: green;
  }
`;
