import React from "react";
import { useNavigate } from "react-router-dom";
import { styled } from "styled-components";
import LogoutButton from "../Checklist/LogoutButton";
import { useAuth0 } from "@auth0/auth0-react";
import { useState } from "react";
import { Icon } from "react-icons-kit";
import { navicon } from "react-icons-kit/fa/navicon";

const Header = () => {
  const navigate = useNavigate();
  const { user } = useAuth0();
  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(!open);
  };
  const handleDropdownClick = (path) => {
    setOpen(false);
    navigate(path);
  };
  return (
    <>
      <HeaderMain>
        <Logo
          onClick={() => {
            navigate("/");
          }}
        >
          Bit
        </Logo>
        {user && (
          <TaskProject>
            <Tasks
              onClick={() => {
                navigate("/checklist");
              }}
            >
              Manage Your Tasks
            </Tasks>
            <Projects
              onClick={() => {
                navigate("/projects");
              }}
            >
              Manage Your Projects
            </Projects>
          </TaskProject>
        )}
        <LogoutButton />
      </HeaderMain>

      {/* ////////////////////////////////// */}

      <DropDown>
        <Logo>Bit</Logo>
        <Main onClick={handleOpen}>
          <Icon icon={navicon} size={30} />
        </Main>
        {open && (
          <DropDownContainer>
            {user && (
              <TaskProject>
                <Tasks onClick={() => handleDropdownClick("/checklist")}>
                  Manage Your Tasks
                </Tasks>
                <Projects onClick={() => handleDropdownClick("/projects")}>
                  Manage Your Projects
                </Projects>
              </TaskProject>
            )}
            <LoginButton />
            <LogoutButton />
          </DropDownContainer>
        )}
      </DropDown>
    </>
  );
};

export default Header;

const HeaderMain = styled.div`
  display: grid;
  grid-template-columns: 10% 50% 40%;
  justify-items: end;
  height: 15%;
  opacity: 1;
  width: 100%;
`;
const DropDown = styled.div`
  display: none;
  margin-bottom: 10%;
  @media only screen and (max-width: 768px) {
    display: flex;
    align-items: center;
    flex-direction: column;
  }
`;
const Main = styled.div`
  cursor: pointer;
`;
const DropDownContainer = styled.div`
  display: flex;
  flex-direction: column;
`;

const Logo = styled.div`
  color: #00c4cc;
  opacity: 1;
  font-size: 8rem;
  margin-top: 2rem;
`;

const TaskProject = styled.div`
  display: flex;

  gap: 10%;
`;

const Projects = styled.button`
  color: #00c4cc;
  opacity: 1;
  border: none;
  font-size: 2rem;
  background-color: transparent;

  &:hover {
    color: #cb6ce6;
  }
  &:active {
    scale: 0.9;
  }
`;
const Tasks = styled.button`
  color: #00c4cc;
  opacity: 1;
  border: none;
  font-size: 2rem;
  background-color: transparent;

  &:hover {
    color: #cb6ce6;
  }
  &:active {
    scale: 0.9;
  }
`;
