import { styled, keyframes } from "styled-components";
import Header from "../Checklist/Header";
import { useEffect, useState } from "react";

const Projects = () => {
  return (
    <Main>
      <Header />
      <Body>
        <SideBar>
          <NewProject>Add Project</NewProject>
        </SideBar>

        <ProjectsOverview>
          <Text>Project Overview</Text>
          <ProjectsTable></ProjectsTable>
        </ProjectsOverview>

        <GradientBox></GradientBox>
      </Body>
    </Main>
  );
};

export default Projects;
const Main = styled.div`
  height: 100vh;
  width: 100vw;
  margin: 0 auto;
`;

const Body = styled.div`
  background-image: url("./images/stickynote.png");
  height: 100%;
  width: 100%;
  display: flex;
  position: relative;
  align-items: center;
`;
const gradientAnimation = keyframes`
0% {
  background-position: 0% 50%;
}
50% {
  background-position: 100% 50%;
}
100% {
  background-position: 0% 50%;
}
`;
const GradientBox = styled.div`
  position: absolute;
  width: 100%;
  height: 70%;
  bottom: 0;
  background-image: linear-gradient(-30deg, #00c4cc, #ff66c4);
  background-size: 200%;
  animation: ${gradientAnimation} 10s linear infinite;
  transform: skewY(-10deg);
  z-index: -2;
`;
const SideBar = styled.div`
  max-width: 25%;
  height: fit-content;
  margin: 5%;
  background-color: #00c4cc;
  color: white;
  border-radius: 0.5rem;
  font-size: 1.5rem;
`;

const NewProject = styled.button``;
const ProjectsOverview = styled.dv`
  z-inex: 2;
  width: 50%;
  height: 100%;
  margin: 5%;
  display: flex;
  flex-direction: column;
  align-items: center;
  border-radius: 0.5rem;
`;
