import React from "react";
import { useNavigate } from "react-router-dom";
import { styled } from "styled-components";
import LoginButton from "./LoginButton";
import LogoutButton from "./LogoutButton";
import { useAuth0 } from "@auth0/auth0-react";
import { Icon } from "react-icons-kit";
import { navicon } from "react-icons-kit/fa/navicon";
import { useState } from "react";

const Header = () => {
  const navigate = useNavigate();
  const { user } = useAuth0();
  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(!open);
  };
  const handleDropdownClick = (path) => {
    setOpen(false);
    navigate(path);
  };

  return (
    <>
      <DropDown>
        <Logo>Bit</Logo>
        <Main onClick={handleOpen}>
          <Icon icon={navicon} size={50} />
          {open ? (
            <>
              {user && (
                <TaskProject>
                  <Tasks
                    onClick={() => {
                      navigate("/checklist");
                    }}
                  >
                    Manage Your Tasks
                  </Tasks>
                  <Projects
                    onClick={() => {
                      navigate("/projects");
                    }}
                  >
                    Manage Your Projects
                  </Projects>
                </TaskProject>
              )}
            </>
          ) : null}

          <LoginButton />
          <LogoutButton />
        </Main>
      </DropDown>
      <HeaderMain>
        <Logo>Bit</Logo>
        {user && (
          <TaskProject>
            <Tasks
              onClick={() => {
                navigate("/checklist");
              }}
            >
              Manage Your Tasks
            </Tasks>
            <Projects
              onClick={() => {
                navigate("/projects");
              }}
            >
              Manage Your Projects
            </Projects>
          </TaskProject>
        )}
        <LoginButton />
        <LogoutButton />
      </HeaderMain>
    </>
  );
};

export default Header;

const DropDown = styled.div`
  display: flex;
  flex-direction: row;
  display: none;
  @media only screen and (max-width: 700px) {
    display: block;
  }
`;
const Main = styled.div`
  display: flex;
  flex-direction: column;
`;

const HeaderMain = styled.div`
  display: grid;
  grid-template-columns: 10% 60% 20%;
  align-items: center;
  justify-items: end;
  height: 15%;
  opacity: 1;
  width: 100%;
  @media (max-width: 1500px) {
    grid-template-columns: 25% 55% 20%;
  }
  @media only screen and (max-width: 700px) {
    display: none;
  }
`;
const Logo = styled.div`
  color: white;
  opacity: 1;
  font-size: 8rem;
  margin-top: 1%;
`;

const TaskProject = styled.div`
  display: flex;
  gap: 10%;
`;

const Projects = styled.button`
  color: white;
  opacity: 1;
  border: none;
  font-size: 2rem;
  background-color: transparent;
  &:hover {
    color: yellow;
  }
  &:active {
    scale: 0.9;
  }
`;
const Tasks = styled.button`
  color: white;
  opacity: 1;
  border: none;
  font-size: 2rem;
  background-color: transparent;

  &:hover {
    color: yellow;
  }
  &:active {
    scale: 0.9;
  }
`;
